interface Product{
    name:string,
    img:string,
    quantity:number,
    id:number,
    price:number,
}
interface Cart{
    name:string;
    id:number,
    price:number,
    img:string,
    number:number,
    quantity:number,
}
interface CartList{
    carts:Cart[],
}
export default function Total() {
  let [productList,setProductList]=useState<Product[]>(function(){
        let productList: Product[] = JSON.parse(localStorage.getItem('products') || '[]');
        if(productList.length===0){
            return [];
        }
        return productList;
    });

    let [cartList,setCart]=useState<Cart[]>(function(){
        let cartList: Cart[] = JSON.parse(localStorage.getItem('carts') || '[]');
        if(cartList.length===0){
            return [];
        }
        return cartList;
    });
    const addProduct=(id:number)=>{
        let findItem:Product|undefined=productList.find(e=>{
            return e.id===id;
        })
        if(findItem){
            let find:Cart|undefined=cartList.find((e)=>{
                return e.id===findItem.id;
            })
            if(!find){
                setCart(prevState=>{
                    const list:Cart[]=[...prevState,{
                        name:findItem.name,
                        id:findItem.id,
                        img:findItem.img,
                        price:findItem.price,
                        number:1,
                        quantity:findItem.quantity,
                    }];
                    localStorage.setItem('carts',JSON.stringify(list));
                    return list;
                });
            }else{
               handleClickIncrease(find)
            }
        }
    }
    const cartTotal=()=>{
        let cartTotal:number=0;
        cartList.forEach((e:Cart)=>{
            cartTotal+=e.number
        })
        return cartTotal;
    }
    const handleClickIncrease=(cartItem:Cart)=>{
        const index :number|undefined=cartList.findIndex(e=>{
            return e.id==cartItem.id;
        })
        const newCart=[...cartList];
        if(newCart[index].quantity<newCart[index].number+1){
            alert('Số lương hàng trong kho đã hết');
            return;
        }
        newCart[index].number++;
        setCart(newCart);
        localStorage.setItem('carts',JSON.stringify(newCart));
    }
    const handleClickDescrease=(cartItem:Cart)=>{
        const index :number|undefined=cartList.findIndex(e=>{
            return e.id==cartItem.id;
        })
        const newCart=[...cartList];
        if(newCart[index].number-1===0){
            if(confirm('Bạn có muốn xóa sản phẩm khỏi giỏ hàng ko?')){
                handleClickRemove(cartItem);
            }
            return;
        }
        newCart[index].number--;
        setCart(newCart);
        localStorage.setItem('carts',JSON.stringify(newCart));
    }
    const handleClickRemove=(cartItem:Cart)=>{
        const newCart=cartList.filter((e)=>{
            return cartItem.id!=e.id;
        })
        setCart(newCart)
        localStorage.setItem('carts',JSON.stringify(newCart));
    }
    const MoneyCart=()=>{
        let cartMoney=0;
        cartList.forEach((e:Cart)=>{
            cartMoney+=e.number*e.price;
        })
        return cartMoney;
    }
  return (
    <div>
        <Header 
            cartTotal={cartTotal()}
            carts={cartList} 
            money={MoneyCart()}
            onClickIncrease={handleClickIncrease} 
            onClickDescrease={handleClickDescrease}
            onClickRemove={handleClickRemove}
        ></Header>
        <div className='productContainer'>
            {productList.map((e,index)=><Item key={index} item={e} addProduct={()=>{addProduct(e.id)}} ></Item>)}
        </div>
    </div>
  )
}
